/**
 * @回到顶部
 */

 'use strict';

import React, { Component } from 'react';
import {
    View,
    Text,
    Image,
    StyleSheet,
    TouchableOpacity,
    Dimensions
} from 'react-native';

let backToTopImage = 'data:image/png;base64,';

export default class ScrollToTop extends Component {

    render() {
        let config = this.props;

        return (
            <TouchableOpacity 
                onPress={this._onPress.bind(this)} 
                style={[styles.toTop, {
                    borderRadius: config.isRadius? config.bdRadius : 0,
                    backgroundColor: config.bgColor?config.bgColor:config.text?'#099fde':null, 
                    width: config.width, 
                    height: config.height,
                    left: config.left,
                    top: config.top
                }]}>
                {config.text?
                    <Text 
                        style={{
                            color: config.color, 
                            fontSize: config.fontSize
                        }}>
                        {config.text}
                    </Text>
                    :
                    <Image 
                        style={{
                            width: config.width, 
                            height: config.height
                        }} 
                        source={{
                            uri: config.imageUri
                        }} />
                }
            </TouchableOpacity>
        );
    }

    _onPress() {
        /*
         * android，ios都使用原生下拉刷新组件：
         */
        // this.props.root.refs.listview.scrollTo({x:0, y:0, animated:true});
        var arr = this.props.refData;
        for(var i=0;i<arr.length;i++){

            if(this.props.root.refs[arr[i]])
            {
                this.props.root.refs[arr[i]].scrollTo({x:0, y:0, animated:true});    
            }    
        }
    }
}

ScrollToTop.defaultProps = {
    isRadius: true,
    width: 60,
    height: 60,
    left: Dimensions.get('window').width - 80,
    top: Dimensions.get('window').height - 160,
    bdRadius: 30,
    color: '#ffffff',
    fontSize: 12,
    imageUri: backToTopImage
}

let styles = StyleSheet.create({
    toTop: {
        flex: 1,
        position: 'absolute',
        alignItems: 'center',
        justifyContent: 'center',
    },
})